export class PostComponent {
  constructor(httpCaller, http, authService, modalService, renderer, scriptService) {
    this.httpCaller = httpCaller;
    this.http = http;
    this.authService = authService;
    this.modalService = modalService;
    this.renderer = renderer;
    this.scriptService = scriptService;
    this.modal = false;
    this.postNum = '';
    this.category = '';
    this.title = '';
    this.writer = '';
    this.date = '';
    this.views = 0;
    this.diagnosis = '';
    this.contents = '';
    this.categories = [{
      id: 1,
      category: "Liver"
    }, {
      id: 2,
      category: "Heart"
    }, {
      id: 3,
      category: "Stomach"
    }];
    this.editFileSource = [];
    this.editFile = []; // this.router.routeReuseStrategy.shouldReuseRoute = () => false;
  }

  ngOnInit() {
    this.getPostList();
    this.authService.currentUser().getUserData((err, data) => {
      if (err) console.log("유저정보를 불러오는데 실패했습니다.", err); // console.log(data)

      this.userName = data.UserAttributes[3].Value;
      this.userId = this.authService.currentUser().getUsername();
    });
  }

  showHide(obj) {
    if (obj.clicked == true) {
      if (obj.text != '') this.editCommentText = obj.text;
      obj.clicked = false;
    } else obj.clicked = true;
  }

  getPostList() {
    this.httpCaller.get('/post/list').subscribe({
      next: data => {
        this.postList = JSON.parse(data.body);
        this.editFileSource = [];
        console.log(this.postList);
      },
      error: error => {
        console.error('There was an error!', error);
      }
    });
  }

  editPost(post) {
    console.log(post);

    if (post.user.name == this.userName && post.user.id == this.userId) {
      //수정 가능
      // let comment_id = comment.comment_id;\
      console.log(this.editCategory, this.editDiagnosis, this.editTitle, this.editDescription);
      let postdct = {
        post_id: post.post_id,
        diagnosis: this.editDiagnosis,
        category: this.editCategory,
        description: this.editDescription,
        title: this.editTitle,
        file_source: this.editFileSource
      };
      console.log(postdct);
      this.http.put('https://ssokuycqjh.execute-api.ap-northeast-2.amazonaws.com/post', postdct).subscribe(res => {
        console.log(res);
        this.editFileSource = [];
        this.getPostList();
      });
    }
  }

  deletePost(post) {
    const options = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json'
      }),
      body: {
        "post_id": post.post_id
      }
    };

    if (this.confirmWindow('게시물을 삭제하시겠습니까?') == true) {
      this.http.delete('https://ssokuycqjh.execute-api.ap-northeast-2.amazonaws.com/post', options).subscribe(res => {
        this.getPostList();
      });
    }
  }

  deleteComment(cmt_id) {
    const options = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json'
      }),
      body: {
        "comment_id": cmt_id
      }
    };

    if (this.confirmWindow('댓글을 삭제하시겠습니까?') == true) {
      this.http.delete('https://ssokuycqjh.execute-api.ap-northeast-2.amazonaws.com/post/comment', options).subscribe(res => {
        this.getPostList();
      });
    }
  }

  editComment(comment) {
    let cmt_id = comment[0]; // let comment_id = comment.comment_id;

    let cmt = {
      text: this.editCommentText
    }; // console.log('before', (<HTMLTextAreaElement> document.getElementById('edit_txt')).value)

    let update_cmt = {
      "comment_id": cmt_id,
      "comment": cmt
    };
    this.http.put('https://ssokuycqjh.execute-api.ap-northeast-2.amazonaws.com/post/comment', update_cmt).subscribe(res => {
      console.log(update_cmt); // console.log(res);

      this.editCommentText = '';
      comment[1].editClicked = false;
      this.getPostList();
    }); // console.log('after', (<HTMLTextAreaElement> document.getElementById('edit_txt')).value)
  }

  thumbsupdownComment(comment, flag) {
    let cmt_id = comment[0];
    let cmt;

    if (flag == 1) {
      if (this.confirmWindow('댓글을 추천하시겠습니까?') == true) cmt = {
        like: true,
        dislike: false
      };
    } else {
      if (this.confirmWindow('댓글을 반대하시겠습니까?') == true) cmt = {
        like: false,
        dislike: true
      };
    }

    let update_cmt = {
      "comment_id": cmt_id,
      "comment": cmt
    };
    console.log(update_cmt);
    this.http.put('https://ssokuycqjh.execute-api.ap-northeast-2.amazonaws.com/post/comment/likedislike', update_cmt).subscribe(res => {
      console.log(res);
      this.getPostList();
    });
  }

  getPostInfo(post) {
    post.edited = true;
    this.editCategory = post.category;
    this.editTitle = post.title;
    this.editDiagnosis = post.diagnosis;
    this.editDescription = post.description;
    console.log('getPostInfo', post.file_source);

    for (let fileSource of post.file_source) {
      this.editFileSource.push(fileSource);
    }
  }

  readURL(event) {
    if (event.target.files && event.target.files.length) {
      const fileList = event.target.files;

      for (const file of fileList) {
        this.readImage(file);
      }
    }
  }

  readImage(file) {
    console.log(file);

    if (file.type && !file.type.startsWith('image/')) {
      console.log('File is not an image.', file.type, file);
      return;
    }

    const reader = new FileReader();

    reader.onload = () => {
      this.editFileSource.push(reader.result);
    };

    reader.addEventListener('progress', event => {
      if (event.loaded && event.total) {
        const percent = event.loaded / event.total * 100;
        console.log(`Progress: ${Math.round(percent)}`);
      }
    });
    reader.readAsDataURL(file);
  }

  deleteImg(imgSrc) {
    console.log(this.editFileSource);

    for (const [i, value] of this.editFileSource.entries()) {
      if (value == imgSrc) {
        this.editFileSource.splice(i, 1);
      }
    }
  }

  confirmWindow(str) {
    let ans = confirm(str);
    return ans;
  }

  postComment(post) {
    let txt = document.getElementById('comment_txt');
    let today = new Date();
    let cmt = {
      text: txt.value,
      write_time: today.toLocaleDateString(),
      dislike: 0,
      like: 0,
      editClicked: false,
      user: {
        id: this.userId,
        name: this.userName
      }
    };
    let comment = {
      "post_id": post.post_id,
      comment: cmt
    };
    this.http.post('https://ssokuycqjh.execute-api.ap-northeast-2.amazonaws.com/post/comment', comment).subscribe(res => {
      console.log(res);
      txt.value = '';
      post.isComment = false; //  window.location.reload(); 전체 새로고침
      // this.router.navigate(['/post']); post component로 이동해버림
      // this.ngOnInit(); 좋지 않은 방법

      this.getPostList();
    });
  }

  loadScript() {
    console.log('preparing to laod');
    const scriptElement = this.scriptService.loadJsScript(this.renderer, '../dist/MyWebApp.js');

    scriptElement.onload = () => {
      console.log('onload');
    }; // let node = document.createElement('script')
    // node.src = '../dist/MyWebApp.js'
    // node.type='text/javascript'
    // node.async= true;
    // document.getElementsByTagName('head')[0].appendChild(node)

  }

  open(content) {
    //   this.loadAPI = new Promise((resolve) => {
    //     console.log('resolving promise...');
    //     this.loadScript();
    // });
    this.modalService.open(content, {
      ariaLabelledBy: 'modal-basic-title'
    }).result.then(result => {
      this.closeResult = `Closed with: ${result}`;
    }, reason => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  getDismissReason(reason) {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }

}

